mapper.select:
    select a.id,a.name,u.name as userName,a.startDate,a.endDate,a.createTime from tbl_activity as a join tbl_user as u on a.owner = u.id  

        a.name like '%' #{name} '%'


        and u.name like '%' #{owner} '%'


        and a.startDate >= #{startDate}


        and a.endDate <= #{endDate}


order by a.createTime desc limit #{skipCount} , #{pageSizeInt}

select count(*) from tbl_activity as a join tbl_user as u on a.owner = u.id  

    a.name like '%' #{name} '%'


    and u.name like '%' #{owner} '%'


    and a.startDate >= #{startDate}


    and a.endDate <= #{endDate}
